import React from 'react';
import SSVLogo from '../assets/img/ssv.png';
import FortaLogo from '../assets/img/forta.svg';
import MoonbeamLogo from '../assets/img/moonbeam.svg';
import StreamerLogo from '../assets/img/streamr.png';

const goodCases = [
  {
    name: 'Forta',
    logo: FortaLogo,
    caseInfo: (
      <p>
        затраты = $70 <br /> срок = 6 месяц <br /> награды = 40 токенов ~ $450
      </p>
    ),
    isBig: false,
  },
  {
    name: 'SSV',
    logo: SSVLogo,
    caseInfo: (
      <p>
        затраты = $300 <br /> срок = 5 месяц <br /> награды = от $2000 до $10000
      </p>
    ),
    isBig: true,
  },
  {
    name: 'Streamer',
    logo: StreamerLogo,
    caseInfo: (
      <p>
        затраты $30 <br /> срок = 3 месяца <br /> награды = 50 токенов ~ $2.5
      </p>
    ),
    isBig: true,
  },
  {
    name: 'Moonbeam',
    logo: MoonbeamLogo,
    caseInfo: 'затраты = $600 \n срок = 9 месяц \n награды = от $20000 до $30000',
    isBig: false,
  },
];

const CaseCard = () => {
  return (
    <div className="p-0 md-p-5">
      <h2 className="white fs-l3 fw-900 text-center">Наши кейсы</h2>
      <div className="flex flex-wrap p-0 md-p-5 case-card container">
        {goodCases.map((obj) => (
          <div key={obj.name} className="card md-p-5">
            <div className="face face1">
              <div className="content">
                {obj.isBig ? (
                  <img className="imgSmall" src={obj.logo} alt={obj.name} />
                ) : (
                  <img src={obj.logo} alt={obj.name} />
                )}
              </div>
            </div>
            <div className="face face2">
              <div className="content">
                <h2 className="text-center">{obj.name}</h2>
                <p>{obj.caseInfo}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CaseCard;
