const Recipient = require('mailersend').Recipient;
const EmailParams = require('mailersend').EmailParams;
const MailerSend = require('mailersend');

let newLine = '\n';

const mailersend = new MailerSend({
  api_key:
    'eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiJ9..AHNW1ipH03K0lWANkl6tpyamOA1dpXes3UWmxabYFM-ZgkLc-WxxGHz7tz_UV4FHd8uDLyj10vEuN2HAe77mJYoSHYpz2vv_s6W5guTwcMoSseltcFlruZcNW0QuQc_4SMztLjuFLQBPCegyYzKMpsg6hoOiMi_7-oDvp6Llsup4sTwmSUPxq9_tElAZokYFLZsFylgZ0UM-Vj2gTIssMJ28cGiYEeNGDVfqfTzo08wGQpXPZcmOhvhPWvJ4UaK_QforxsgWbhe-4pFusM7kEQh39CDClCB2j0JDsLCRUdjTQjYUoI3TVfZeBiL_85GwFAE0gBe5QYjau6NkYJNu_tUSZBCt4INi_prZv6SCVi4f7omO5aJIQJdkyZkGQ5AdA20RT56UPwKMXImD-cbIw4l7_OSeC-Lw3pdbfurTLH2-t-lv2GdFzi9a3WI6RgwWVhPweUxc0_R1WftI-Ylhxm6pVFaf-E46XeODhODsdPFrBQPknEAr4HuHXu0_AuMVUU7CPsIiay0_sl4Lwt-YunDd5_Z0wiHI1DXiUb1upvZmm4374hVfb8nFF6anQi_ATqFny4WhKPu4wdFFTmCJjUGOsKhkFIHo20FeoPJhLdYb5fzDlM-JPB3-AbxTFizEbK4BDvV9hYIfn4y7vAUJR6cuEBbH2uKmkoI8ZWqTnlU',
});

const from = 'donotreply@smarthamster.tech';
const recipients = [new Recipient('sh.node.studio@gmail.com', 'sh.node.studio')];

export function sendContactMeMessageToEmail(name, question) {
  let emailMessage =
    '–ü—Ä–∏–≤–µ—Ç, –∑–∞–∏–Ω—Ç–µ—Ä–µ—Å–æ–≤–∞–ª—Å—è –≤–∞—à–∏–º –∫—É—Ä—Å–æ–º, —Å–≤—è–∂–∏—Ç–µ—Å—å —Å–æ –º–Ω–æ–π:' +
    name +
    newLine +
    '–£ –º–µ–Ω—è –≤–æ–∑–Ω–∏–∫ —Å–ª–µ–¥—É—é—â–∏–π –≤–æ–ø—Ä–æ—Å: ' +
    question +
    newLine;

  const subject = '–ó–∞–ø—Ä–æ—Å –Ω–∞ –æ–±—Ä–∞—Ç–Ω—É—é —Å–≤—è–∑—å —Å —Å–∞–π—Ç–∞ smarthamster.tech –æ—Ç –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è ' + name;

  const emailParams = new EmailParams()
    .setFrom(from)
    .setRecipients(recipients)
    .setSubject(subject)
    .setText(emailMessage);

  mailersend.send(emailParams);
}

export function sendBuyMessageToEmail(name, email, telegramId, course, txHash) {
  let emailMessage =
    'ü•≥ –ü–æ–∫—É–ø–∫–∞ –∫—É—Ä—Å–∞ ü•≥ :' +
    newLine +
    '–ò–º—è ' +
    name +
    newLine +
    '–ü–æ—á—Ç–∞ ' +
    email +
    newLine +
    '–¢–µ–ª–µ–≥—Ä–∞–º–º ' +
    telegramId +
    newLine +
    '–ö—É—Ä—Å ' +
    course +
    newLine +
    '–•—ç—à —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏ ' +
    txHash;

  const subject = 'ü•≥ –ü–æ–∫—É–ø–∫–∞ –∫—É—Ä—Å–∞ ü•≥ —Å —Å–∞–π—Ç–∞ smarthamster.tech –æ—Ç –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è ' + name;

  const emailParams = new EmailParams()
    .setFrom(from)
    .setRecipients(recipients)
    .setSubject(subject)
    .setText(emailMessage);

  mailersend.send(emailParams);
}
