import React from 'react';

const BuyConfirmation = ({ refer, closeWindow }) => {
  const [name, setName] = React.useState('');
  const [email, setEmail] = React.useState('');
  const [telegramId, setTelegramId] = React.useState('');
  const [course, setCourse] = React.useState('');
  const [txHash, setTxHash] = React.useState('');
  const [showThanksMessage, setShowThanksMessage] = React.useState(false);

  const onChangeName = (event) => {
    setName(evant.target.value);
  };

  const onChangeEmail = (event) => {
    setEmail(evant.target.value);
  };

  const onChangeTelegramId = (event) => {
    setTelegramId(evant.target.value);
  };

  const onChangeCourse = (event) => {
    setCourse(evant.target.value);
  };

  const onChangeTxHash = (event) => {
    setTxHash(evant.target.value);
  };

  const onClickConfirm = () => {};

  return (
    <div className="login-popup bg-black p-5 md-p-l4 flex flex-wrap md-justify-between md-items-center show">
      <div className="box bg-indigo-lightest-10 scrool">
        <div className="img-area">
          <div className="img"></div>
          <h1 className="black text-center">
            Smart
            <br />
            Hamster
            <br />
            Nodes
            <br />
            Studio
          </h1>
        </div>
        <div className="form center scrool text-center">
          <div className="close white" onClick={() => closeWindow(false)}>
            <p>&times;</p>
          </div>
          <h1 className="white fs-m4 fw-400 text-center">Подтверждение покупки</h1>
          <div className="indigo-lightest fw-50 fs-s1 opacity-50 text-center break scrool">
            Указывайте email и хэш транзакции. Мы отправим на него доступ к курсу.
          </div>
          <form id="payment-form" className="white py-5 scrool">
            <div className="form-group">
              <input
                id="form-name"
                type="text"
                placeholder="Твое имя"
                className="white form-control input-lg flex-grow-1 bw-0 fw-200 bg-indigo-lightest-10 ph-white"
              />
            </div>
            <div className="form-group">
              <input
                id="form-email"
                type="text"
                placeholder="Email для связи"
                className="white form-control input-lg flex-grow-1 bw-0 fw-200 bg-indigo-lightest-10 ph-white"
              />
            </div>
            <div className="form-group">
              <input
                id="form-telegram"
                type="text"
                placeholder="Твой telegram ник"
                className="white form-control input-lg flex-grow-1 bw-0 fw-200 bg-indigo-lightest-10 ph-white"
              />
            </div>
            <div className="form-group">
              <select className="white form-control select flex-grow-1 bw-0 fw-200 bg-indigo-lightest-10 select">
                <option disabled selected value>
                  -- Выбери курс --
                </option>
                <option value="99">PRO nodes 99$</option>
                <option value="149">PRO nodes + 1 нода 149$</option>
                <option value="199">PRO nodes + 3 нод 199$</option>
              </select>
            </div>
            <div className="form-group">
              <input
                id="form-hash"
                type="text"
                placeholder="Введи hash транзакции"
                className="white form-control input-lg flex-grow-1 bw-0 fw-200 bg-indigo-lightest-10 ph-white"
              />
            </div>
            <button className="button-lg bg-indigo indigo-lightest fw-300 fs-s3 sendPaymentResponse mt-5">
              Подтвердить покупку
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default BuyConfirmation;
