import React from 'react';
import { Check, X } from 'react-feather';

const courcePrice = [
  {
    title: 'PRO nodes',
    price: 99,
    options: [
      {
        optionName: 'Видео-уроки',
        active: true,
      },
      {
        optionName: 'Методичка',
        active: true,
      },
      {
        optionName: 'Доступ к закрытому чату',
        active: true,
      },
      {
        optionName: 'Установка нод',
        active: false,
      },
    ],
    isWhite: false,
  },
  {
    title: 'PRO nodes +1 нода',
    price: 149,
    options: [
      {
        optionName: 'Видео-уроки',
        active: true,
      },
      {
        optionName: 'Методичка',
        active: true,
      },
      {
        optionName: 'Доступ к закрытому чату',
        active: true,
      },
      {
        optionName: 'Установка 1й ноды совместно',
        active: true,
      },
    ],
    isWhite: true,
  },
  {
    title: 'PRO nodes +3 ноды',
    price: 199,
    options: [
      {
        optionName: 'Видео-уроки',
        active: true,
      },
      {
        optionName: 'Методичка',
        active: true,
      },
      {
        optionName: 'Доступ к закрытому чату',
        active: true,
      },
      {
        optionName: 'Установка 3х нод совместно',
        active: true,
      },
    ],
    isWhite: false,
  },
];
const BuyCourse = ({}) => {
  return (
    <div id="pricing">
      <div className="p-10 flex flex-wrap">
        <div className="w-100pc md-w-25pc">
          <div className="p-5">
            <h4 className="white fw-800 fs-l3 mb-5">Курсы</h4>
            <div className="indigo-lightest fw-600 fs-m1 opacity-50">
              Выбирай подходящий тебе тариф и скорей присоединяйся к нам!
            </div>
            <div className="flex indigo-lightest opacity-50"></div>
          </div>
        </div>
        {courcePrice.map((obj) => (
          <div className="w-100pc md-w-25pc">
            <div
              className={`m-3 p-5 br-8 ${
                obj.isWhite ? 'bg-white' : 'bg-indigo-lightest-10'
              } overflow-hidden`}
            >
              <div className="p-3">
                <h3 className="indigo">{obj.title}</h3>
                <div className="white flex items-center">
                  $<span className="fs-l5 lh-1">{obj.price}</span>
                </div>
              </div>
              <div className="p-3 indigo-lightest fw-400 fs-s1 lh-5">
                {obj.options.map((option) =>
                  option.active ? (
                    <div>
                      <Check className="h-3 indigo" strokeWidth="4" />
                      <span className="opacity-50">{option.optionName}</span>
                    </div>
                  ) : (
                    <div>
                      <X className="h-3 indigo" strokeWidth="4" />
                      <span className="opacity-50">{option.optionName}</span>
                    </div>
                  ),
                )}
              </div>
              <div className="p-3">
                <div className="button full bg-black white hover-opacity-100 hover-scale-up-1 ease-300">
                  Купить
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BuyCourse;
