import i18next from "i18next";
import { initReactI18next } from "react-i18next";
import LanguageDetector from "i18next-browser-languagedetector";
//import {translations} from "./translations";
const translations = {
    uk: {
        translation: {
            navBar: {},
            welcomeBlock:{},
            aboutCourceBlock:{},
            courseContent:{},
            photoBaner:{},
            buyCourse:{},
            caroucel:{},
            forWho:{},
            card:{},
            team:{},
            caseCard:{},
            contactMe:{},
            app_name: "Компания",
            footer:{

                aboutUs: "О Нас",
                product: "Продукт",
                aboutCourse: "О Курсе",
                butCourse: "Купить Курс"
            },
            thanksBlock:{}
        },
    }
};

i18next
    .use(initReactI18next)
    .use(LanguageDetector)
    .init({
        translations,
        fallbackLng: "eng",
        interpolation: {
            escapeValue: false,
        },
    });
export default i18next;
